    15→  private elementProcessors: Map<string, IElementProcessor> = new Map();
    16→
    17→  constructor(
    18→    private fileService: IFileService,
    19→    private xmlParser: IXmlParseService,
    20→    private imageDataService?: ImageDataService
    21→  ) {}
