    82→    if (!bgNode) {
    83→      return undefined;
    84→    }
    85→
    86→    const bgPrNode = this.xmlParser.findNode(bgNode, 'bgPr');
    87→    if (!bgPrNode) {
    88→      return undefined;
    89→    }
    90→
    91→    // Check for solid fill
    92→    const solidFillNode = this.xmlParser.findNode(bgPrNode, 'solidFill');
