    94→      const color = this.extractColor(solidFillNode);
    95→      if (color) {
    96→        return {
    97→          type: 'solid',
    98→          color
    99→        };
   100→      }
   101→    }
   102→
   103→    // Check for gradient fill
   104→    const gradFillNode = this.xmlParser.findNode(bgPrNode, 'gradFill');
   105→    if (gradFillNode) {
   106→      const colors = this.extractGradientColors(gradFillNode);
   107→      if (colors.length > 0) {
   108→        return {
   109→          type: 'gradient',
   110→          colors
   111→        };
   112→      }
   113→    }
