   136→              // Fall back to relationship-based URL
   137→            }
   138→          }
   139→          
   140→          // Fallback: resolve from relationships for backward compatibility
   141→          let resolvedUrl = embedId;
   142→          if (context.relationships.has(embedId)) {
   143→            const rel = context.relationships.get(embedId);
   144→            if (rel && rel.target) {
   145→              resolvedUrl = rel.target;
   146→            }
   147→          }
   148→          
