   160→  private async parseElements(slideNode: XmlNode, context: ProcessingContext): Promise<Element[]> {
   161→    const elements: Element[] = [];
   162→    const spTreeNode = this.xmlParser.findNode(slideNode, 'spTree');
   163→    
   164→    if (!spTreeNode || !spTreeNode.children) {
   165→      return elements;
   166→    }
   167→
