    90→        success: false,
    91→        imageId: imageData.hash,
    92→        metadata: {
    93→          uploadTime: new Date(),
    94→          size: imageData.size,
    95→          format: imageData.format
    96→        },
    97→        error: error instanceof Error ? error.message : 'Unknown error'
    98→      };
    99→    }
   100→  }
   101→
   102→  async generateUrl(imageId: string): Promise<string> {
   103→    // Base64 模式下，URL 就是数据本身，这里无法生成独立的 URL
   104→    throw new Error('Base64 strategy stores data inline, use upload result URL');
   105→  }
   106→
   107→  async checkAvailability(): Promise<boolean> {
   108→    return true; // Base64 总是可用
   109→  }
   110→
   111→  async healthCheck(): Promise<HealthStatus> {
   112→    return {
   113→      healthy: true,
   114→      latency: 0,
   115→      errorRate: 0,
   116→      lastCheck: new Date(),
   117→      details: 'Base64 encoding always available'
   118→    };
   119→  }
   120→}
   121→
   122→/**
   123→ * 图片存储管理器
   124→ */
   125→export class ImageStorageManager {
   126→  private strategies: Map<string, ImageStorageStrategy> = new Map();
   127→  private primaryStrategy: ImageStorageStrategy;
   128→
   129→  constructor() {
   130→    // 默认使用 Base64 策略
   131→    const base64Strategy = new Base64StorageStrategy();
   132→    this.strategies.set(base64Strategy.name, base64Strategy);
   133→    this.primaryStrategy = base64Strategy;
   134→  }
   135→
   136→  /**
   137→   * 注册新的存储策略
   138→   */
   139→  registerStrategy(strategy: ImageStorageStrategy): void {
   140→    this.strategies.set(strategy.name, strategy);
   141→    
   142→    // 如果新策略优先级更高，设为主策略
   143→    if (strategy.priority > this.primaryStrategy.priority) {
   144→      this.primaryStrategy = strategy;
   145→    }
   146→  }
   147→
   148→  /**
   149→   * 设置主要存储策略
   150→   */
   151→  setPrimaryStrategy(strategyName: string): void {
   152→    const strategy = this.strategies.get(strategyName);
   153→    if (strategy) {
   154→      this.primaryStrategy = strategy;
   155→    } else {
   156→      throw new Error(`Strategy '${strategyName}' not found`);
   157→    }
   158→  }
   159→
   160→  /**
   161→   * 获取当前主要策略
   162→   */
   163→  getPrimaryStrategy(): ImageStorageStrategy {
   164→    return this.primaryStrategy;
   165→  }
   166→
   167→  /**
   168→   * 处理图片上传
   169→   */
   170→  async processImage(imageData: ImageUploadData, options?: UploadOptions): Promise<StorageResult> {
   171→    try {
   172→      // 检查主策略是否可用
   173→      if (await this.primaryStrategy.checkAvailability()) {
   174→        return await this.primaryStrategy.upload(imageData, options);
   175→      }
   176→      
   177→      // 主策略不可用，尝试回退策略
   178→      const base64Strategy = this.strategies.get('base64');
   179→      if (base64Strategy && base64Strategy !== this.primaryStrategy) {
   180→        console.warn(`Primary strategy '${this.primaryStrategy.name}' unavailable, falling back to base64`);
   181→        return await base64Strategy.upload(imageData, options);
   182→      }
   183→      
   184→      throw new Error('No available storage strategy');
   185→      
   186→    } catch (error) {
   187→      console.error('Image storage failed:', error);
   188→      throw error;
   189→    }
   190→  }
   191→
   192→  /**
   193→   * 获取所有策略的健康状态
   194→   */
   195→  async getHealthStatus(): Promise<Map<string, HealthStatus>> {
   196→    const statuses = new Map<string, HealthStatus>();
   197→    
   198→    for (const [name, strategy] of this.strategies) {
   199→      try {
   200→        const status = await strategy.healthCheck();
   201→        statuses.set(name, status);
   202→      } catch (error) {
   203→        statuses.set(name, {
   204→          healthy: false,
   205→          lastCheck: new Date(),
   206→          details: error instanceof Error ? error.message : 'Health check failed'
   207→        });
   208→      }
   209→    }
   210→    
   211→    return statuses;
   212→  }
   213→}

