                 xmlns:r="http://schemas.openxmlformats.org/officeDocument/2006/relationships">
          <sld:cSld>
            <sld:bg>
              <sld:bgPr>
                <a:blipFill>
                  <a:blip r:embed="rId1"/>
                </a:blipFill>
              </sld:bgPr>
            </sld:bg>
