     1â†’import { IElementProcessor } from "../../interfaces/IElementProcessor";
     2â†’import { ProcessingContext } from "../../interfaces/ProcessingContext";
     3â†’import {
     4â†’  ShapeElement,
     5â†’  ShapeType,
     6â†’  GradientFill,
     7â†’} from "../../../models/domain/elements/ShapeElement";
     8â†’import { XmlNode } from "../../../models/xml/XmlNode";
     9â†’import { IXmlParseService } from "../../interfaces/IXmlParseService";
    10â†’import { UnitConverter } from "../../utils/UnitConverter";
    11â†’import { FillExtractor } from "../../utils/FillExtractor";
    12â†’import {
    13â†’  SHAPE_LIST,
    14â†’  SHAPE_PATH_FORMULAS,
    15â†’  ShapePoolItem,
    16â†’} from "../../../utils";
    17â†’import { TextContent } from "../../../models/domain/elements/TextElement";
    18â†’import { DebugHelper } from "../../utils/DebugHelper";
    19â†’import { HtmlConverter } from "../../utils/HtmlConverter";
    20â†’import { TextStyleExtractor } from "../../text/TextStyleExtractor";
    21â†’
    22â†’export class ShapeProcessor implements IElementProcessor<ShapeElement> {
    23â†’  private textStyleExtractor: TextStyleExtractor;
    24â†’  private lastProcessedParagraphs: TextContent[][] = [];
    25â†’
    26â†’  constructor(private xmlParser: IXmlParseService) {
    27â†’    this.textStyleExtractor = new TextStyleExtractor(xmlParser);
    28â†’  }
    29â†’
    30â†’  canProcess(xmlNode: XmlNode): boolean {
    31â†’    // Skip if it's explicitly a text box
    32â†’    const nvSpPrNode = this.xmlParser.findNode(xmlNode, "nvSpPr");
    33â†’    const cNvSpPrNode = nvSpPrNode
    34â†’      ? this.xmlParser.findNode(nvSpPrNode, "cNvSpPr")
    35â†’      : undefined;
    36â†’    const txBox = cNvSpPrNode
    37â†’      ? this.xmlParser.getAttribute(cNvSpPrNode, "txBox")
    38â†’      : undefined;
    39â†’
    40â†’    if (txBox === "1") {
    41â†’      // This is a text box, should be handled by TextProcessor
    42â†’      return false;
    43â†’    }
    44â†’
    45â†’    // Process shape nodes that have visible shape backgrounds and don't have image fill
    46â†’    // This includes pure shapes and shapes with text (for the background shape)
    47â†’    return (
    48â†’      xmlNode.name.endsWith("sp") &&
    49â†’      this.hasVisibleShapeBackground(xmlNode) &&
    50â†’      !this.hasImageFill(xmlNode)
    51â†’    );
    52â†’  }
    53â†’
    54â†’  async process(
    55â†’    xmlNode: XmlNode,
    56â†’    context: ProcessingContext
    57â†’  ): Promise<ShapeElement> {
    58â†’    DebugHelper.log(context, "=== Starting Shape Processing ===", "info");
    59â†’
    60â†’    const shapeList: ShapePoolItem[] = [];
    61â†’    for (const item of SHAPE_LIST) {
    62â†’      shapeList.push(...item.children);
    63â†’    }
    64â†’    // Extract shape ID
    65â†’    const nvSpPrNode = this.xmlParser.findNode(xmlNode, "nvSpPr");
    66â†’    const cNvPrNode = nvSpPrNode
    67â†’      ? this.xmlParser.findNode(nvSpPrNode, "cNvPr")
    68â†’      : undefined;
    69â†’    const originalId = cNvPrNode
    70â†’      ? this.xmlParser.getAttribute(cNvPrNode, "id")
    71â†’      : undefined;
    72â†’
    73â†’    // Generate unique ID
    74â†’    const id = context.idGenerator.generateUniqueId(originalId, "shape");
    75â†’
    76â†’    // Extract geometry first to determine shape type
    77â†’    const spPrNode = this.xmlParser.findNode(xmlNode, "spPr");
    78â†’    let shapeType: ShapeType = "rect"; // default
    79â†’    let adjustmentValues: Record<string, number> = {};
    80â†’
    81â†’    if (spPrNode) {
    82â†’      const prstGeomNode = this.xmlParser.findNode(spPrNode, "prstGeom");
    83â†’      if (prstGeomNode) {
    84â†’        const prst = this.xmlParser.getAttribute(prstGeomNode, "prst");
    85â†’        if (prst) {
    86â†’          shapeType = this.mapGeometryToShapeType(prst);
    87â†’          DebugHelper.log(
    88â†’            context,
    89â†’            `Shape geometry: ${prst} -> ${shapeType}`,
    90â†’            "info"
    91â†’          );
    92â†’
    93â†’          // Extract adjustment values for roundRect shapes
    94â†’          if (prst === "roundRect") {
    95â†’            adjustmentValues = this.extractAdjustmentValues(prstGeomNode);
    96â†’            DebugHelper.log(
    97â†’              context,
    98â†’              `RoundRect adjustments:`,
    99â†’              "info",
   100â†’              adjustmentValues
   101â†’            );
   102â†’          }
   103â†’        }
   104â†’      } else {
   105â†’        // Check for custom geometry
   106â†’        const custGeomNode = this.xmlParser.findNode(spPrNode, "custGeom");
   107â†’        if (custGeomNode) {
   108â†’          // For custom geometry, try to detect if it's circular
   109â†’          shapeType = this.analyzeCustomGeometry(custGeomNode);
   110â†’          // Don't set pathFormula for custom geometry
   111â†’        }
   112â†’      }
   113â†’    }
   114â†’
   115â†’    const shapeElement = new ShapeElement(id, shapeType);
   116â†’
   117â†’    // Set adjustment values if available
   118â†’    if (Object.keys(adjustmentValues).length > 0) {
   119â†’      shapeElement.setAdjustmentValues(adjustmentValues);
   120â†’    }
   121â†’
   122â†’    // Extract position and size first, considering group transforms
   123â†’    let width = 0,
   124â†’      height = 0;
   125â†’    if (spPrNode) {
   126â†’      const xfrmNode = this.xmlParser.findNode(spPrNode, "xfrm");
   127â†’      if (xfrmNode) {
   128â†’        // Get group transform from context
   129â†’        const groupTransform = context.groupTransform;
   130â†’
   131â†’        // Position
   132â†’        const offNode = this.xmlParser.findNode(xfrmNode, "off");
   133â†’        if (offNode) {
   134â†’          const x = this.xmlParser.getAttribute(offNode, "x");
   135â†’          const y = this.xmlParser.getAttribute(offNode, "y");
   136â†’          if (x && y) {
   137â†’            let posX = parseInt(x);
   138â†’            let posY = parseInt(y);
   139â†’
   140â†’            // Apply group transform if exists - using the correct 4-step formula
   141â†’            if (
   142â†’              groupTransform &&
   143â†’              groupTransform.offset &&
   144â†’              groupTransform.childOffset
   145â†’            ) {
   146â†’              // æ­¥éª¤1: ç¼©æ”¾æ¯”ä¾‹å·²åœ¨ groupTransform ä¸­è®¡ç®—
   147â†’              const scaleX = groupTransform.scaleX;
   148â†’              const scaleY = groupTransform.scaleY;
   149â†’
   150â†’              // æ­¥éª¤2: è®¡ç®—å½¢çŠ¶ç›¸å¯¹äºå­ç©ºé—´åŸç‚¹çš„ä½ç½®
   151â†’              const relativeX = posX - groupTransform.childOffset.x;
   152â†’              const relativeY = posY - groupTransform.childOffset.y;
   153â†’
   154â†’              // æ­¥éª¤3: åº”ç”¨ç¼©æ”¾å˜æ¢
   155â†’              const scaledX = relativeX * scaleX;
   156â†’              const scaledY = relativeY * scaleY;
   157â†’
   158â†’              // æ­¥éª¤4: åŠ ä¸Šç»„åˆåœ¨å¹»ç¯ç‰‡ä¸­çš„ä½ç½®
   159â†’              const finalX = scaledX + groupTransform.offset.x;
   160â†’              const finalY = scaledY + groupTransform.offset.y;
   161â†’
   162â†’              posX = finalX;
   163â†’              posY = finalY;
   164â†’
   165â†’              DebugHelper.log(
   166â†’                context,
   167â†’                `Applied group transform: relative(${relativeX}, ${relativeY}) -> scaled(${scaledX.toFixed(
   168â†’                  2
   169â†’                )}, ${scaledY.toFixed(2)}) -> final(${finalX}, ${finalY})`,
   170â†’                "info"
   171â†’              );
   172â†’            }
   173â†’
   174â†’            const finalPosX = UnitConverter.emuToPointsPrecise(posX);
   175â†’            const finalPosY = UnitConverter.emuToPointsPrecise(posY);
   176â†’
   177â†’            shapeElement.setPosition({ x: finalPosX, y: finalPosY });
   178â†’            DebugHelper.log(
   179â†’              context,
   180â†’              `Shape position: (${finalPosX}, ${finalPosY})`,
   181â†’              "info"
   182â†’            );
   183â†’          }
   184â†’        }
   185â†’
   186â†’        // Size
   187â†’        const extNode = this.xmlParser.findNode(xfrmNode, "ext");
   188â†’        if (extNode) {
   189â†’          const cx = this.xmlParser.getAttribute(extNode, "cx");
   190â†’          const cy = this.xmlParser.getAttribute(extNode, "cy");
   191â†’          if (cx && cy) {
   192â†’            width = UnitConverter.emuToPointsPrecise(parseInt(cx));
   193â†’            height = UnitConverter.emuToPointsPrecise(parseInt(cy));
   194â†’
   195â†’            // // Apply group transform if exists
   196â†’            // if (groupTransform) {
   197â†’            //   width = width * groupTransform.scaleX;
   198â†’            //   height = height * groupTransform.scaleY;
   199â†’            //   DebugHelper.log(
   200â†’            //     context,
   201â†’            //     `Applied group transform to size: ${width} x ${height}`,
   202â†’            //     "info"
   203â†’            //   );
   204â†’            // }
   205â†’
   206â†’            shapeElement.setSize({
   207â†’              width,
   208â†’              height,
   209â†’            });
   210â†’            DebugHelper.log(
   211â†’              context,
   212â†’              `Shape size: ${width} x ${height}`,
   213â†’              "info"
   214â†’            );
   215â†’          }
   216â†’        }
   217â†’
   218â†’        // Rotation
   219â†’        const rot = this.xmlParser.getAttribute(xfrmNode, "rot");
   220â†’        if (rot) {
   221â†’          shapeElement.setRotation(parseInt(rot) / 60000); // Convert to degrees
   222â†’        }
   223â†’      }
   224â†’
   225â†’      // Extract SVG path from custom geometry or generate from preset
   226â†’      const custGeomNode = this.xmlParser.findNode(spPrNode, "custGeom");
   227â†’      if (custGeomNode) {
   228â†’        // Handle custom geometry
   229â†’        const svgPath = this.extractSvgPath(custGeomNode, width, height);
   230â†’        if (svgPath && svgPath.trim().length > 10) { // Check for meaningful path content
   231â†’          shapeElement.setPath(svgPath);
   232â†’          // For custom geometry, calculate viewBox from path range
   233â†’          if (svgPath.indexOf("NaN") === -1) {
   234â†’            const { maxX, maxY } = this.getSvgPathRange(svgPath);
   235â†’            shapeElement.setViewBox([maxX || width, maxY || height]);
   236â†’          }
   237â†’        } else {
   238â†’          // Custom geometry failed, fall back to detected shape type or rect
   239â†’          const fallbackShapeType = shapeType !== "custom" ? shapeType.toString() : "rect";
   240â†’          DebugHelper.log(
   241â†’            context,
   242â†’            `Custom geometry extraction failed, falling back to detected shape type: ${fallbackShapeType}`,
   243â†’            "warn"
   244â†’          );
   245â†’          const fallbackPath = this.getShapePath(fallbackShapeType, width, height);
   246â†’          if (fallbackPath) {
   247â†’            shapeElement.setPath(fallbackPath);
   248â†’            shapeElement.setViewBox([width, height]);
   249â†’          }
   250â†’        }
   251â†’      } else {
   252â†’        // Handle preset geometry
   253â†’        const prstGeomNode = this.xmlParser.findNode(spPrNode, "prstGeom");
   254â†’        if (prstGeomNode) {
   255â†’          const prst = this.xmlParser.getAttribute(prstGeomNode, "prst");
   256â†’          if (prst) {
   257â†’            // Find matching shape in SHAPE_LIST
   258â†’            const shape = shapeList.find((item) => item.pptxShapeType === prst);
   259â†’
   260â†’            if (shape) {
   261â†’              // Use shape's predefined path and viewBox
   262â†’              shapeElement.setPath(shape.path);
   263â†’              shapeElement.setViewBox(shape.viewBox);
   264â†’
   265â†’              // If shape has pathFormula in SHAPE_PATH_FORMULAS
   266â†’              if (shape.pathFormula && SHAPE_PATH_FORMULAS[shape.pathFormula]) {
   267â†’                const pathFormulaConfig =
   268â†’                  SHAPE_PATH_FORMULAS[shape.pathFormula];
   269â†’                shapeElement.setPathFormula(shape.pathFormula);
   270â†’                shapeElement.setViewBox([width, height]); // Use actual dimensions
   271â†’
   272â†’                // Generate path using formula
   273â†’                if (
   274â†’                  "editable" in pathFormulaConfig &&
   275â†’                  pathFormulaConfig.editable
   276â†’                ) {
   277â†’                  const defaultValues = pathFormulaConfig.defaultValue || [0.5];
   278â†’                  // Use adjustment values if available, otherwise use defaults
   279â†’                  const useValues =
   280â†’                    Object.keys(adjustmentValues).length > 0
   281â†’                      ? [adjustmentValues.adj || defaultValues[0]]
   282â†’                      : defaultValues;
   283â†’                  shapeElement.setPath(
   284â†’                    pathFormulaConfig.formula(width, height, useValues)
   285â†’                  );
   286â†’                  if (Object.keys(adjustmentValues).length === 0) {
   287â†’                    shapeElement.setAdjustmentValues({ adj: defaultValues[0] });
   288â†’                  }
   289â†’                } else {
   290â†’                  shapeElement.setPath(
   291â†’                    pathFormulaConfig.formula(width, height)
   292â†’                  );
   293â†’                }
   294â†’              }
   295â†’
   296â†’              // Mark as special if shape has special flag
   297â†’              if (shape.special) {
   298â†’                shapeElement.setSpecial(true);
   299â†’              }
   300â†’            } else {
   301â†’              // Fallback: generate path using dimensions
   302â†’              const pathWidth = width || 200;
   303â†’              const pathHeight = height || 200;
   304â†’              const svgPath = this.getShapePath(
   305â†’                prst,
   306â†’                pathWidth,
   307â†’                pathHeight,
   308â†’                adjustmentValues
   309â†’              );
   310â†’              if (svgPath) {
   311â†’                shapeElement.setPath(svgPath);
   312â†’              }
   313â†’              // Set viewBox to actual dimensions for preset shapes
   314â†’              shapeElement.setViewBox([pathWidth, pathHeight]);
   315â†’            }
   316â†’          }
   317â†’        }
   318â†’      }
   319â†’
   320â†’      // Extract style node for style references (fillRef, lnRef, etc.)
   321â†’      const styleNode = this.xmlParser.findNode(xmlNode, "style");
   322â†’
   323â†’      // Check for gradient fill first
   324â†’      const gradFillNode = this.xmlParser.findNode(spPrNode, "gradFill");
   325â†’      if (gradFillNode) {
   326â†’        const gradient = this.extractGradientFill(gradFillNode, context);
   327â†’        if (gradient) {
   328â†’          shapeElement.setGradient(gradient);
   329â†’          DebugHelper.log(context, `Shape gradient set`, "success");
   330â†’        }
   331â†’      } else {
   332â†’        // Extract fill color - improved extraction with style references
   333â†’        const fillColor = this.extractFillColor(spPrNode, context, styleNode);
   334â†’        if (fillColor) {
   335â†’          shapeElement.setFill({ color: fillColor });
   336â†’          DebugHelper.log(
   337â†’            context,
   338â†’            `Shape fill color set to: ${fillColor}`,
   339â†’            "success"
   340â†’          );
   341â†’        } else {
   342â†’          // Check if this is a shape that should have a default gradient
   343â†’          if (this.shouldHaveDefaultGradient(shapeElement)) {
   344â†’            const defaultGradient = this.createDefaultGradient(shapeElement);
   345â†’            if (defaultGradient) {
   346â†’              shapeElement.setGradient(defaultGradient);
   347â†’              DebugHelper.log(
   348â†’                context,
   349â†’                `Applied default gradient for shape type: ${shapeElement.getShapeType()}`,
   350â†’                "info"
   351â†’              );
   352â†’            }
   353â†’          }
   354â†’
   355â†’          // Always log for debugging
   356â†’          DebugHelper.log(
   357â†’            context,
   358â†’            `No fill color resolved for shape ${shapeElement.getId()}, path: ${shapeElement
   359â†’              .getPath()
   360â†’              ?.substring(0, 50)}...`,
   361â†’            "warn"
   362â†’          );
   363â†’        }
   364â†’      }
   365â†’    }
   366â†’
   367â†’    // Extract style node for text style inheritance
   368â†’    const styleNode = this.xmlParser.findNode(xmlNode, "style");
   369â†’
   370â†’    // Extract text content if present
   371â†’    const txBodyNode = this.xmlParser.findNode(xmlNode, "txBody");
   372â†’    if (txBodyNode) {
   373â†’      // Pass shape style for inheritance
   374â†’      const textContent = this.extractTextContentUsingSharedLogic(
   375â†’        txBodyNode,
   376â†’        context,
   377â†’        styleNode
   378â†’      );
   379â†’      if (textContent && textContent.length > 0) {
   380â†’        // Create shape text content in PPTist format
   381â†’        const shapeTextContent = this.createShapeTextContent(
   382â†’          textContent,
   383â†’          txBodyNode
   384â†’        );
   385â†’        shapeElement.setShapeTextContent(shapeTextContent);
   386â†’      }
   387â†’    }
   388â†’
   389â†’    return shapeElement;
   390â†’  }
   391â†’
   392â†’  getElementType(): string {
   393â†’    return "shape";
   394â†’  }
   395â†’
   396â†’  private hasImageFill(xmlNode: XmlNode): boolean {
   397â†’    // Check if shape has blipFill (image fill)
   398â†’    const spPrNode = this.xmlParser.findNode(xmlNode, "spPr");
   399â†’    if (!spPrNode) return false;
   400â†’
   401â†’    const blipFillNode = this.xmlParser.findNode(spPrNode, "blipFill");
   402â†’    return !!blipFillNode;
   403â†’  }
   404â†’
   405â†’
   406â†’  private hasVisibleShapeBackground(xmlNode: XmlNode): boolean {
   407â†’    // Check if shape has visible background fill
   408â†’    const spPrNode = this.xmlParser.findNode(xmlNode, "spPr");
   409â†’    if (!spPrNode) return false;
   410â†’
   411â†’    // Check for explicit fills
   412â†’    const solidFillNode = this.xmlParser.findNode(spPrNode, "solidFill");
   413â†’    const gradFillNode = this.xmlParser.findNode(spPrNode, "gradFill");
   414â†’    const pattFillNode = this.xmlParser.findNode(spPrNode, "pattFill");
   415â†’
   416â†’    // Check for preset geometry (indicates a shape rather than pure text)
   417â†’    const prstGeomNode = this.xmlParser.findNode(spPrNode, "prstGeom");
   418â†’    const custGeomNode = this.xmlParser.findNode(spPrNode, "custGeom");
   419â†’
   420â†’    // If it has fills OR preset/custom geometry, it's likely a shape
   421â†’    return !!(
   422â†’      solidFillNode ||
   423â†’      gradFillNode ||
   424â†’      pattFillNode ||
   425â†’      prstGeomNode ||
   426â†’      custGeomNode
   427â†’    );
   428â†’  }
   429â†’
   430â†’  private mapGeometryToShapeType(prst: string): ShapeType {
   431â†’    // Map PowerPoint preset geometry to our shape types
   432â†’    const mapping: { [key: string]: ShapeType } = {
   433â†’      rect: "rect",
   434â†’      roundRect: "roundRect",
   435â†’      ellipse: "ellipse",
   436â†’      circle: "ellipse", // Add circle mapping
   437â†’      oval: "ellipse", // Add oval mapping
   438â†’      triangle: "triangle",
   439â†’      diamond: "diamond",
   440â†’      parallelogram: "parallelogram",
   441â†’      trapezoid: "trapezoid",
   442â†’      pentagon: "pentagon",
   443â†’      hexagon: "hexagon",
   444â†’      octagon: "octagon",
   445â†’      star5: "star",
   446â†’      star4: "star",
   447â†’      star6: "star",
   448â†’      rightArrow: "arrow",
   449â†’      leftArrow: "arrow",
   450â†’      upArrow: "arrow",
   451â†’      downArrow: "arrow",
   452â†’      callout1: "callout",
   453â†’      callout2: "callout",
   454â†’      callout3: "callout",
   455â†’    };
   456â†’
   457â†’    return mapping[prst] || "custom";
   458â†’  }
   459â†’
   460â†’  private extractFillColor(
   461â†’    spPrNode: XmlNode,
   462â†’    context: ProcessingContext,
   463â†’    shapeStyleNode?: XmlNode
   464â†’  ): string | undefined {
   465â†’    DebugHelper.log(context, "Starting fill color extraction", "info");
   466â†’
   467â†’    // Check for explicit noFill first (but only direct children of spPr, not in a:ln)
   468â†’    const noFillNode = this.findDirectChildNode(spPrNode, "noFill");
   469â†’    if (noFillNode) {
   470â†’      DebugHelper.log(
   471â†’        context,
   472â†’        "Found explicit noFill, returning transparent",
   473â†’        "info"
   474â†’      );
   475â†’      return "rgba(0,0,0,0)"; // Transparent
   476â†’    }
   477â†’
   478â†’    // NEW: Check for style references first (fillRef from p:style)
   479â†’    if (shapeStyleNode) {
   480â†’      DebugHelper.log(context, "Processing shape style references", "info");
   481â†’      const styleColor = this.extractColorFromStyleRef(shapeStyleNode, context);
   482â†’      if (styleColor) {
   483â†’        DebugHelper.log(
   484â†’          context,
   485â†’          `Style reference resolved to: ${styleColor}`,
   486â†’          "success"
   487â†’        );
   488â†’        return styleColor;
   489â†’      }
   490â†’    }
   491â†’
   492â†’    // Check for direct solidFill in spPr (but only direct children of spPr, not in a:ln)
   493â†’    const solidFillNode = this.findDirectChildNode(spPrNode, "solidFill");
   494â†’    if (solidFillNode) {
   495â†’      const solidFillObj = this.xmlNodeToObject(solidFillNode);
   496â†’
   497â†’      // Only require theme if this solidFill contains scheme color references
   498â†’      const hasSchemeColor = solidFillObj["a:schemeClr"];
   499â†’      if (hasSchemeColor && !context.theme) {
   500â†’        throw new Error(
   501â†’          "ShapeProcessor: ProcessingContext.theme is null/undefined - cannot process scheme colors. Found schemeClr reference but no theme available."
   502â†’        );
   503â†’      }
   504â†’
   505â†’      const warpObj = {
   506â†’        themeContent:
   507â†’          context.theme && hasSchemeColor
   508â†’            ? this.createThemeContent(context.theme)
   509â†’            : undefined,
   510â†’      };
   511â†’
   512â†’      const color = FillExtractor.getSolidFill(
   513â†’        solidFillObj,
   514â†’        undefined,
   515â†’        undefined,
   516â†’        warpObj
   517â†’      );
   518â†’
