   633â†’
   634â†’    // Add children
   635â†’    if (node.children && node.children.length > 0) {
   636â†’      for (const child of node.children) {
   637â†’        const childName = child.name.includes(":")
   638â†’          ? child.name
   639â†’          : `a:${child.name}`;
   640â†’        obj[childName] = this.xmlNodeToObject(child);
