   647â†’  private hasSchemeColorReference(obj: any): boolean {
   648â†’    if (!obj || typeof obj !== "object") return false;
   649â†’
   650â†’    // Check if this object or any nested object contains a:schemeClr
   651â†’    if (obj["a:schemeClr"]) return true;
   652â†’
   653â†’    // Recursively check nested objects
   654â†’    for (const key in obj) {
   655â†’      if (
   656â†’        typeof obj[key] === "object" &&
   657â†’        this.hasSchemeColorReference(obj[key])
   658â†’      ) {
   659â†’        return true;
   660â†’      }
   661â†’    }
   662â†’
   663â†’    return false;
