   807â†’  /**
   808â†’   * Analyzes custom geometry to determine if it represents a known shape type
   809â†’   */
   810â†’  private analyzeCustomGeometry(custGeomNode: XmlNode): ShapeType {
   811â†’    const pathLstNode = this.xmlParser.findNode(custGeomNode, "pathLst");
   812â†’    if (!pathLstNode) return "custom";
   813â†’
   814â†’    const pathNode = this.xmlParser.findNode(pathLstNode, "path");
   815â†’    if (!pathNode) return "custom";
   816â†’
   817â†’    // Get path dimensions
   818â†’    const w = this.xmlParser.getAttribute(pathNode, "w");
   819â†’    const h = this.xmlParser.getAttribute(pathNode, "h");
   820â†’
   821â†’    // Check for arc commands which typically indicate circular/elliptical shapes
   822â†’    const arcNodes = this.xmlParser.findNodes(pathNode, "arcTo");
   823â†’    if (arcNodes.length > 0) {
   824â†’      // If dimensions are equal, it's likely a circle
   825â†’      if (w === h) {
   826â†’        return "ellipse";
   827â†’      }
   828â†’      // Different dimensions but has arcs, likely an ellipse
   829â†’      return "ellipse";
   830â†’    }
   831â†’
   832â†’    // Check if it's square with cubic BÃ©zier curves (alternative circle representation)
   833â†’    if (w === h) {
   834â†’      // Look for cubic BÃ©zier patterns that indicate circular geometry
   835â†’      const cubicBezNodes = this.xmlParser.findNodes(pathNode, "cubicBezTo");
   836â†’
   837â†’      // Circular custom geometry typically has 4 cubic BÃ©zier curves
   838â†’      if (cubicBezNodes.length === 4) {
   839â†’        // Check if it starts from center-top (typical circle pattern)
   840â†’        const moveToNode = this.xmlParser.findNode(pathNode, "moveTo");
   841â†’        if (moveToNode) {
   842â†’          const ptNode = this.xmlParser.findNode(moveToNode, "pt");
   843â†’          if (ptNode) {
   844â†’            const x = this.xmlParser.getAttribute(ptNode, "x");
   845â†’            const pathWidth = parseInt(w || "0");
   846â†’            const centerX = pathWidth / 2;
   847â†’
   848â†’            // If starts from approximately center-top, likely a circle
   849â†’            if (Math.abs(parseInt(x || "0") - centerX) < pathWidth * 0.1) {
   850â†’              return "ellipse";
   851â†’            }
   852â†’          }
   853â†’        }
   854â†’      }
   855â†’    }
   856â†’
   857â†’    // Check for simple rectangle patterns
   858â†’    const lineToNodes = this.xmlParser.findNodes(pathNode, "lnTo");
   859â†’    const closeNodes = this.xmlParser.findNodes(pathNode, "close");
   860â†’
   861â†’    if (lineToNodes.length === 3 && closeNodes.length === 1) {
   862â†’      // Likely a rectangle (moveTo + 3 lineTo + close)
   863â†’      return "rect";
   864â†’    }
   865â†’
   866â†’    return "custom";
   867â†’  }
