   878â†’    if (!pathLstNode) return undefined;
   879â†’
   880â†’    const pathNode = this.xmlParser.findNode(pathLstNode, "path");
   881â†’    if (!pathNode) return undefined;
   882â†’
   883â†’    // Extract original dimensions from PowerPoint path
   884â†’    const originalWidth = parseInt(
   885â†’      this.xmlParser.getAttribute(pathNode, "w") || "0"
   886â†’    );
   887â†’    const originalHeight = parseInt(
   888â†’      this.xmlParser.getAttribute(pathNode, "h") || "0"
   889â†’    );
