   891â†’    if (originalWidth === 0 || originalHeight === 0) {
   892â†’      return undefined;
   893â†’    }
   894â†’
   895â†’    // Use provided target dimensions or defaults
   896â†’    const finalTargetWidth = targetWidth || 200;
   897â†’    const finalTargetHeight = targetHeight || 200;
   898â†’
   899â†’    // Calculate scale factors: PowerPoint EMU to SVG coordinates
   900â†’    const scaleX = finalTargetWidth / originalWidth;
   901â†’    const scaleY = finalTargetHeight / originalHeight;
   902â†’
   903â†’    // Extract all path commands in XML order
   904â†’    const commands: string[] = [];
   905â†’    const precision = 6; // Coordinate precision
   906â†’
   907â†’    // Process path children in order
   908â†’    if (pathNode.children) {
