   910â†’        switch (child.name) {
   911â†’          case "moveTo":
   912â†’          case "a:moveTo": {
   913â†’            const ptNode = this.xmlParser.findNode(child, "pt");
   914â†’            if (ptNode) {
   915â†’              const x = parseInt(
   916â†’                this.xmlParser.getAttribute(ptNode, "x") || "0"
   917â†’              );
   918â†’              const y = parseInt(
   919â†’                this.xmlParser.getAttribute(ptNode, "y") || "0"
   920â†’              );
   921â†’              const svgX = (x * scaleX).toFixed(precision);
   922â†’              const svgY = (y * scaleY).toFixed(precision);
   923â†’              commands.push(`M${svgX},${svgY}`);
   924â†’            }
   925â†’            break;
   926â†’          }
   927â†’
   928â†’          case "lnTo":
   929â†’          case "a:lnTo": {
   930â†’            const ptNode = this.xmlParser.findNode(child, "pt");
   931â†’            if (ptNode) {
   932â†’              const x = parseInt(
   933â†’                this.xmlParser.getAttribute(ptNode, "x") || "0"
   934â†’              );
   935â†’              const y = parseInt(
   936â†’                this.xmlParser.getAttribute(ptNode, "y") || "0"
   937â†’              );
   938â†’              const svgX = (x * scaleX).toFixed(precision);
   939â†’              const svgY = (y * scaleY).toFixed(precision);
   940â†’              commands.push(`L${svgX},${svgY}`);
   941â†’            }
   942â†’            break;
   943â†’          }
   944â†’
   945â†’          case "cubicBezTo":
   946â†’          case "a:cubicBezTo": {
   947â†’            const ptNodes = this.xmlParser.findNodes(child, "pt");
   948â†’            if (ptNodes && ptNodes.length === 3) {
   949â†’              const coords = ptNodes.map((ptNode) => {
   950â†’                const x = parseInt(
   951â†’                  this.xmlParser.getAttribute(ptNode, "x") || "0"
   952â†’                );
   953â†’                const y = parseInt(
   954â†’                  this.xmlParser.getAttribute(ptNode, "y") || "0"
   955â†’                );
   956â†’                return {
   957â†’                  x: (x * scaleX).toFixed(precision),
   958â†’                  y: (y * scaleY).toFixed(precision),
   959â†’                };
   960â†’              });
   961â†’
   962â†’              commands.push(
   963â†’                `C${coords[0].x},${coords[0].y} ${coords[1].x},${coords[1].y} ${coords[2].x},${coords[2].y}`
   964â†’              );
   965â†’            }
   966â†’            break;
   967â†’          }
   968â†’
   969â†’          case "close":
   970â†’          case "a:close": {
   971â†’            commands.push("Z");
   972â†’            break;
   973â†’          }
   974â†’
   975â†’          case "arcTo":
   976â†’          case "a:arcTo": {
   977â†’            // Convert arc to cubic bezier approximation (simplified)
   978â†’            const wR =
   979â†’              parseInt(this.xmlParser.getAttribute(child, "wR") || "0") *
   980â†’              scaleX;
   981â†’            const hR =
   982â†’              parseInt(this.xmlParser.getAttribute(child, "hR") || "0") *
   983â†’              scaleY;
   984â†’            const swAng =
   985â†’              parseInt(this.xmlParser.getAttribute(child, "swAng") || "0") /
   986â†’              60000;
   987â†’
   988â†’            // Simplified arc handling - for complex arcs, use approximation
   989â†’            if (wR > 0 && hR > 0) {
   990â†’              const largeArc = Math.abs(swAng) > 180 ? 1 : 0;
   991â†’              const sweep = swAng > 0 ? 1 : 0;
   992â†’
   993â†’              // This is a simplified version - full arc implementation would require more complex math
   994â†’              commands.push(
   995â†’                `A${wR.toFixed(precision)},${hR.toFixed(
   996â†’                  precision
   997â†’                )} 0 ${largeArc} ${sweep}`
