  1015â†’  /**
  1016â†’   * Optimize SVG path by removing unnecessary precision and formatting
  1017â†’   */
  1018â†’  private optimizeSvgPath(svgPath: string, maxPrecision: number = 2): string {
  1019â†’    if (!svgPath) return "";
