  1021â†’    // Remove unnecessary precision from decimal numbers
  1022â†’    const optimized = svgPath.replace(/(\d+\.\d+)/g, (match) => {
  1023â†’      const num = parseFloat(match);
  1024â†’      return num.toFixed(maxPrecision).replace(/\.?0+$/, "");
  1025â†’    });
  1026â†’
  1027â†’    // Clean up extra spaces
  1028â†’    return optimized.replace(/\s+/g, " ").trim();
  1029â†’  }
