  1282â†’  private extractAdjustmentValues(prstGeomNode: any): Record<string, number> {
  1283â†’    const adjustmentValues: Record<string, number> = {};
  1284â†’
  1285â†’    // Look for adjustment value list
  1286â†’    const avLstNode = this.xmlParser.findNode(prstGeomNode, "avLst");
  1287â†’    if (avLstNode) {
  1288â†’      const gdNodes = this.xmlParser.findNodes(avLstNode, "gd");
  1289â†’      for (const gdNode of gdNodes) {
  1290â†’        const name = this.xmlParser.getAttribute(gdNode, "name");
  1291â†’        const fmla = this.xmlParser.getAttribute(gdNode, "fmla");
  1292â†’
  1293â†’        if (name && fmla && fmla.startsWith("val ")) {
  1294â†’          // Extract numeric value from formula like "val 50000"
  1295â†’          const value = parseInt(fmla.substring(4));
  1296â†’          // Convert from EMU units to ratio (typically 50000 = 0.5)
  1297â†’          adjustmentValues[name] = value / 100000;
  1298â†’        }
  1299â†’      }
  1300â†’    }
  1301â†’
  1302â†’    return adjustmentValues;
  1303â†’  }
