  1305â†’  /**
  1306â†’   * Extract text content using shared TextStyleExtractor logic
  1307â†’   * This ensures consistency between TextProcessor and ShapeProcessor
  1308â†’   */
  1309â†’  private extractTextContentUsingSharedLogic(
  1310â†’    txBodyNode: XmlNode,
  1311â†’    context: ProcessingContext,
  1312â†’    shapeStyleNode?: XmlNode
  1313â†’  ): TextContent[] {
  1314â†’    // Use unified text extraction logic by paragraphs
  1315â†’    const result = this.textStyleExtractor.extractTextContentByParagraphs(
  1316â†’      txBodyNode,
  1317â†’      context,
  1318â†’      shapeStyleNode
  1319â†’    );
  1320â†’    const paragraphGroups = result.paragraphs;
  1321â†’
  1322â†’    // Apply paragraph alignment processing to each paragraph
  1323â†’    const paragraphNodes = this.xmlParser.findNodes(txBodyNode, "p");
  1324â†’    const processedParagraphs: TextContent[][] = [];
  1325â†’
  1326â†’    for (
  1327â†’      let i = 0;
  1328â†’      i < paragraphGroups.length && i < paragraphNodes.length;
  1329â†’      i++
  1330â†’    ) {
  1331â†’      const paragraphContent = paragraphGroups[i];
  1332â†’      const pNode = paragraphNodes[i];
  1333â†’
  1334â†’      // Extract paragraph properties for alignment
  1335â†’      const pPrNode = this.xmlParser.findNode(pNode, "pPr");
  1336â†’      let paragraphAlign = undefined;
  1337â†’
  1338â†’      if (pPrNode) {
  1339â†’        const algn = this.xmlParser.getAttribute(pPrNode, "algn");
  1340â†’        if (algn) {
  1341â†’          paragraphAlign = this.mapAlignmentToCSS(algn);
  1342â†’        }
  1343â†’      }
  1344â†’
  1345â†’      // Apply paragraph alignment to all content items in this paragraph
  1346â†’      const alignedParagraphContent = paragraphContent.map((content) => ({
  1347â†’        ...content,
  1348â†’        style: {
  1349â†’          ...content.style,
  1350â†’          ...(paragraphAlign && { textAlign: paragraphAlign }),
  1351â†’        },
  1352â†’      }));
  1353â†’
  1354â†’      processedParagraphs.push(alignedParagraphContent);
  1355â†’    }
  1356â†’
  1357â†’    // Store processed paragraphs for later use in createShapeTextContent
  1358â†’    this.lastProcessedParagraphs = processedParagraphs;
  1359â†’
  1360â†’    // Return flattened content for backward compatibility
  1361â†’    return processedParagraphs.flat();
  1362â†’  }
