  1364â†’  private createShapeTextContent(
  1365â†’    contentItems: TextContent[],
  1366â†’    txBodyNode: XmlNode
  1367â†’  ): any {
  1368â†’    // Use processed paragraphs if available, otherwise fall back to single paragraph
  1369â†’    let html: string;
  1370â†’    if (this.lastProcessedParagraphs.length > 0) {
  1371â†’      // Generate HTML with proper paragraph structure
  1372â†’      html = HtmlConverter.convertParagraphsToHtml(
  1373â†’        this.lastProcessedParagraphs,
  1374â†’        {
  1375â†’          wrapInDiv: false, // We'll add our own structure
  1376â†’        }
  1377â†’      );
  1378â†’    } else {
  1379â†’      // Fallback to single paragraph
  1380â†’      html = HtmlConverter.convertSingleParagraphToHtml(contentItems, {
  1381â†’        wrapInDiv: false,
  1382â†’      });
  1383â†’    }
  1384â†’
  1385â†’    // Extract vertical alignment from body properties
  1386â†’    let align = "middle"; // default
  1387â†’    const bodyPrNode = this.xmlParser.findNode(txBodyNode, "bodyPr");
  1388â†’    if (bodyPrNode) {
  1389â†’      const anchor = this.xmlParser.getAttribute(bodyPrNode, "anchor");
  1390â†’      if (anchor === "t") align = "top";
  1391â†’      else if (anchor === "b") align = "bottom";
  1392â†’      else align = "middle";
  1393â†’    }
  1394â†’
  1395â†’    // Get default font and color using HtmlConverter
  1396â†’    const defaultFontName = HtmlConverter.getDefaultFontName(contentItems);
  1397â†’    const defaultColor = HtmlConverter.getDefaultColor(contentItems);
  1398â†’
  1399â†’    return {
  1400â†’      content: html,
  1401â†’      align: align,
  1402â†’      defaultFontName: defaultFontName,
  1403â†’      defaultColor: defaultColor,
  1404â†’    };
  1405â†’  }
