  1407â†’  private mapAlignmentToCSS(algn: string): string {
  1408â†’    switch (algn) {
  1409â†’      case "l":
  1410â†’        return "left";
  1411â†’      case "ctr":
  1412â†’        return "center";
  1413â†’      case "r":
  1414â†’        return "right";
  1415â†’      case "just":
  1416â†’        return "justify";
  1417â†’      default:
  1418â†’        return "left";
  1419â†’    }
  1420â†’  }
