  1427â†’  /**
  1428â†’   * Get the range of an SVG path to determine viewBox
  1429â†’   */
  1430â†’  private getSvgPathRange(path: string): { maxX: number; maxY: number } {
  1431â†’    let maxX = 0;
  1432â†’    let maxY = 0;
  1433â†’
  1434â†’    // Simple regex to extract numeric values from path
  1435â†’    const numRegex = /-?\d+(\.\d+)?/g;
  1436â†’    const matches = path.match(numRegex);
  1437â†’
  1438â†’    if (matches) {
  1439â†’      const numbers = matches.map((n) => parseFloat(n));
  1440â†’      // Process numbers in pairs (x, y)
  1441â†’      for (let i = 0; i < numbers.length - 1; i += 2) {
  1442â†’        const x = Math.abs(numbers[i]);
  1443â†’        const y = Math.abs(numbers[i + 1]);
  1444â†’        maxX = Math.max(maxX, x);
  1445â†’        maxY = Math.max(maxY, y);
  1446â†’      }
  1447â†’    }
  1448â†’
  1449â†’    return { maxX, maxY };
  1450â†’  }
