              createMockXmlNode('a:pt', { x: '150', y: '200' }),
              createMockXmlNode('a:pt', { x: '100', y: '200' })
            ]),
            createMockXmlNode('a:arcTo', { wR: '50', hR: '50', swAng: '21600000' }),
