    it('should handle gradient color modifiers', async () => {
      const gradFill = createMockXmlNode('a:gradFill', {}, [
        createMockXmlNode('a:lin', { ang: '0' }),
        createMockXmlNode('a:gsLst', {}, [
          createMockXmlNode('a:gs', { pos: '0' }, [
