    });
  });

  describe('JSON serialization', () => {
    it('should serialize basic shape with default values', () => {
      const shape = new ShapeElement('test-id', 'rect');

      const json = shape.toJSON();

      expect(json).toEqual({
        type: 'shape',
        id: 'test-id',
        width: 0,
        height: 0,
        left: 0,
        top: 0,
        viewBox: [200, 200],
        path: 'M 0 0 L 200 0 L 200 200 L 0 200 Z',
        fixedRatio: false,
        rotate: 0,
