    it('should handle malformed XML structure gracefully', async () => {
      const malformedXml = createMockXmlNode('p:sp', {}, [
        createMockXmlNode('p:nvSpPr', {}, [
          createMockXmlNode('p:cNvPr', { id: '5', name: 'Malformed TextBox' }),
